import React, { useEffect, useState } from 'react'
import "./Main.css";
import axios from 'axios';
import { calculateTimeDifference } from './Utility';

const Search = () => {



  const [results, setresults]=useState([])
  const [loading, setloading]=useState(false)
  const [error,seterror]=useState(null);
  const [query, setquery]=useState('')

  const [stories, setstories]=useState('')
  const [sortby, setsortby]=useState('popularity')

  const [pagecount, setpagecount]=useState(0)
  console.log(pagecount)



 








const getResults=async()=>{
  setloading(true);
  try{
   
      const response = await axios.get(`https://hn.algolia.com/api/v1/search?query=${query}&tags=${stories}&page=${pagecount}`);
  setresults(response.data.hits);
  console.log(response.data.hits)
 
  } catch(err){
      seterror(err);
  }
  setloading(false);
};


useEffect(()=>{
 
 
  getResults()
  
 
},[]);





const Handlesearch=(e)=>{
  e.preventDefault()
  getResults()
  
 
}

function storychange(e){
  setstories(e.target.value)
  getResults()
  
}

function statechange(e){
  setsortby(e.target.value)
  getResults()
  
}

const Increment=()=>{
  setpagecount(pagecount+1);
  getResults()
}



  return (
    <div>


        <div>
        <button onClick={Increment}>Load more</button>
        </div>
            </div>
  )
}

export default Search
