import React, { useEffect, useState } from 'react'
import axios from 'axios';




import "./Main.css";

function Main() {

    const [results, setresults]=useState([])
    const [loading, setloading]=useState(false)
    const [error,seterror]=useState(null);
    const [query, setquery]=useState('')
  

    const getResults=async()=>{
        setloading(true);
        try{
            const response = await axios.get(`https://hn.algolia.com/api/v1/search_by_date?query=${query}`);
        setresults(response.data.hits);
       
        } catch(err){
            seterror(err);
        }
        setloading(false);
      };
      
      
      useEffect(()=>{
       
       
        getResults()
      
        
      },[]);

      const Handlesearch=(e)=>{
        e.preventDefault()
        getResults()
       
      }
  

  
    return (
      <main data-testid="main" className="stories-container">
        
        <a href='https://news.ycombinator.com/item?id=$' className="card-anchor" target="_blank" rel="noreferrer">
        <article className="story">
          <p className="title">
            hiii
          </p>
          <p className="text">himbikgui</p>
          <p className="story-footer">
            &#128336;  comments
          </p>
        </article>
      </a>



      <a href='https://news.ycombinator.com/item?id=$' className="card-anchor" target="_blank" rel="noreferrer">
        <article className="story">
          <p className="title">
            hiii
          </p>
          <p className="text">himbikgui</p>
          <p className="story-footer">
            &#128336;  comments
          </p>
        </article>
      </a>
        
      </main>
    );
  };



export default Main;
