import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import "./Home.css";

const Home = () => {
  const Url = "https://restcountries.com/v2/all?fields=name,$,flag";



   
 const userid=useParams();

 console.log(userid);

 



  const [data, setdata] = useState(null);
  const fetchdata = () => {
    try {
      axios.get(Url).then((res) => {
        setdata(res.data);
      });
    } catch (error) {
      console.log("ERROR");
    }
  };

  useEffect(() => {
    fetchdata();
  });



  return (
    <div>

      

      <div className="cards">
        {data &&
          data.map((item, i) => (
            <div  key={i}>
              <div class="card">
                
                <div><img src={item.flag} alt=""></img></div>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.region}</p>
                  </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
