import React, { useEffect, useState } from 'react'
import "./Main.css";
import axios from 'axios';
import { calculateTimeDifference } from './Utility';

const Search = () => {



  const [results, setresults]=useState([])
  const [loading, setloading]=useState(false)
  const [error,seterror]=useState(null);
  const [query, setquery]=useState('')
  const [stories, setstories]=useState('')
  const [sortby, setsortby]=useState('popularity')
  const [pagecount, setpagecount]=useState(0)

  const Increment=()=>{
    setpagecount(pagecount+1);
    getResults()
  }

const getResults=async()=>{
  setloading(true);
  try{           
   
      const response = await axios.get(`https://hn.algolia.com/api/v1/search?query=${query}&type=comment`);
  setresults(response.data.hits);
  console.log(response.data.hits)
 
  } catch(err){
      seterror(err);
  }
  setloading(false);
};


useEffect(()=>{
 
 
  getResults()
  
 
},[]);





const Handlesearch=(e)=>{
  e.preventDefault()
  getResults()
  
 
}

function storychange(e){
  setstories(e.target.value)
  getResults()
  
}

function statechange(e){
  setsortby(e.target.value)
  getResults()
  
}





  return (
    <div>


<div >
        <div className='searchcontainer'>
        <form onSubmit={Handlesearch}>
        <input value={query} onChange={(e)=>setquery(e.target.value)} className='searchinput' placeholder='  search..'></input>
        <button type='submit' className='searchtbtn'>SEARCH</button>
        </form> 
        </div>      
<div>

<div></div>
</div>


      
        
        {loading ? (
            <div className='check'>loading.....</div>) :
            (
                // <ul>


                <main data-testid="main" className="stories-container">

                {results.map((result)=>
                    <>
                <a href={result.url} className="card-anchor" target="_blank" rel="noreferrer">
                  <article className="story">
                  <p className="title">
                    {result.title}
                  </p>
                  <p className="text">{result.article}</p>
                  <p className="story-footer">
                   {result.descendants} comments
                  </p>
                </article>
                </a>

                </>
                    )}




                    


<div>
        <button onClick={Increment}>Load more</button>
        </div>


</main>


                    
            )
        }
        

        {error && <div>{error.message}</div>}
        
        </div>


        
            </div>
  )
}

export default Search
