import React, { useState } from "react";


import "./Main.css";

const Todo = () => {


  const prom= new Promise(function(resolve,reject){
    const complete=true;
    if(complete===true){
      resolve('true bhaii')
    }else{
      reject('false bhaii')
    }
  });


prom.then((m)=>{
  console.log(m);
}).catch((err)=>{
  console.log(err);
})





  
  const [todo, Settodo] = useState('');
  const [data, setdata]=useState([])


  function handlesubmit (){

    setdata([...data,todo])
    Settodo('');
   
  }
  
  console.log(data)
  return (
    <div>
      <div className="container">
        <div className="subcontainer">
        <h1>Todo List</h1><br></br>
        <input value={todo} required="required"  onChange={(e)=>Settodo(e.target.value)} ></input>
        <button onClick={handlesubmit}>Add</button>
        <div>
        {data.map((data,i)=>{
          return (
            <p className="displaydata" key={i}>{data}</p>
          )
        })}
        </div>
        </div>
        
      </div>
    </div>
  );
};



export default Todo;
