import axios from "axios";
import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import "./Home.css";

const Home = () => {
 

  const [data, setdata] = useState();
  const [regions, setregions] = useState('region');
  


  

  useEffect(() => {
    const fetchdata = () => {
      try {
        axios.get(`https://restcountries.com/v2/all?fields=name,${regions},flag`).then((res) => {
          setdata(res.data);
        });
      } catch (error) {
        console.log("ERROR");
      }
    };


    fetchdata();
  },[data,regions]);

  console.log(data);


  const Asia=()=> {
    setregions('asia')

  }

  const Europe=()=> {
    setregions('europe')
    setdata('e')
  }

 

  return (
    <div>

<div>
      <nav className="navbar">
        <label className="navbar-toggle" id="js-navbar-toggle" for="chkToggle">
          <i className="fa fa-bars"></i>
        </label>
        <p className="logo">
          Countries
        </p>
        <input type="checkbox" id="chkToggle"></input>
        <ul className="main-nav" id="js-menu">
          <li>
            <p onClick={Asia} className="nav-links">
              All
            </p>
          </li>
          <li>
            <NavLink onClick={Asia} className="nav-links">
              Asia
            </NavLink>
          </li>
          <li>
            <button onClick={Europe} className="nav-links">
              Europe
            </button>
          </li>
        </ul>
      </nav>
    </div>



      <div className="cards">
        {data &&
          data.map((item, i) => (
            <div  key={i}>
              <div class="card">
                
                <div><img src={item.flag} alt=""></img></div>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.region}</p>
                  </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
