import React, { useEffect, useState } from 'react'
import axios from 'axios';
import './Main.css'
import { calculateTimeDifference } from './Utility';

const Topstories = () => {

    const BASE_API_URL = 'https://hacker-news.firebaseio.com/v0';

    const [results, setresults]=useState([])
    const [loading, setloading]=useState(false)
  const [error,seterror]=useState(null);

const getStory = async (id) => {

    
  try {
    const story = await axios.get(`${BASE_API_URL}/item/${id}.json`);
    return story;
  } catch (error) {
    console.log('Error while getting a story.');
    seterror(error);
  }
  
};

const getStories = async () => {
    setloading(true);
  try {
    const { data: storyIds } = await axios.get(
      `${BASE_API_URL}/topstories.json`
    );
    const stories = await Promise.all(storyIds.slice(0, 30).map(getStory));
    setresults(stories);
    console.log(stories)
  } catch (error) {
    console.log('Error while getting list of stories.');
  }
  setloading(false);
};

useEffect(()=>{
 
 
    getStories()
  
    
  },[]);





  return (
    <div>
        {loading ? (
            <div className='check'>loading.....</div>) :
            (
                // <ul>


                <main data-testid="main" className="stories-container">

                {results.map((result)=>
                    <>
                <a href={result.data.url} className="card-anchor" target="_blank" rel="noreferrer">
                  <article className="story">
                  <p className="title">
                    {result.data.title}
                  </p>
                  <p className="text">{result.data.article}</p>
                  <p className="story-footer">
                  {calculateTimeDifference(Date.now(), result.data.time)} ago | {result.data.descendants} comments
                  </p>
                </article>
                </a>

                </>
                    )}

</main>


                    
            )
        }

        {error && <div>{error.message}</div>}
    </div>
  )
}

export default Topstories
