import React, { useEffect, useState } from 'react'
import axios from 'axios';
import ReactPaginate from 'react-paginate';

import './Main.css'
import { calculateTimeDifference } from './Utility';



const Topstories = () => {
  const url = 'https://hacker-news.firebaseio.com/v0';

  

  const [topstories, settopstories]=useState([])
  const [loading, setloading]=useState(false)
  const [error,seterror]=useState(null);
  const [pagecount, setpagecount]=useState(0)

  const [currentPage, setcurrentPage] = useState(0);

  

const getStory = async (id) => {

    
  try {
    const story = await axios.get(`${url}/item/${id}.json`);
    return story;
  } catch (error) {
    seterror(error);
  }
  
};

const getStories = async () => {
    setloading(true);
  try {
    const { data: storyIds } = await axios.get(`${url}/topstories.json`);
    const stories = await Promise.all(storyIds.slice(0, 30).map(getStory));
    settopstories(stories);
  } catch (error) {
    seterror(error);
  }
  setloading(false);
};



useEffect(()=>{
    getStories()
  },[]);



  const Increment=()=>{
    setpagecount(pagecount+1);
    getStories()
  }



  return (
    
    <div>
        {loading ? (
            <div className='loading'>loading.....</div>) :
            (
                <div  className="stories-container">

                {topstories.map((result)=>
                    <>
                <a href={result.data.url} className="card-anchor" target="_blank" rel="noreferrer">
                  <article className="story">
                  <p className="title">
                    {result.data.title}
                  </p>
                  <p className="text">{result.data.article}</p>
                  <p className="story-footer">
                  {calculateTimeDifference(Date.now(), result.data.time)} ago | {result.data.descendants} comments
                  </p>
                </article>
                </a>

                </>
                    )}

<div>
{isLoaded ? (
				<ReactPaginate
					pageCount={pageCount}
					pageRange={2}
					marginPagesDisplayed={2}
					onPageChange={handlePageChange}
					containerClassName={'container'}
					previousLinkClassName={'page'}
					breakClassName={'page'}
					nextLinkClassName={'page'}
					pageClassName={'page'}
					disabledClassNae={'disabled'}
					activeClassName={'active'}
				/>
			) : (
				<div>Nothing to display</div>
			)} 
        </div>

</div>


                    
            )
        }

        {error && <div>{error.message}</div>}
    </div>
    
  )
}

export default Topstories
