import React, { useState } from "react";


import "./Main.css";

const Todo = () => {


  async function getdata(){
    return 'Data Received'
  }
  
  console.log(getdata());


  
  const [todo, Settodo] = useState('');
  const [data, setdata]=useState([])


  function handlesubmit (){

    setdata([...data,todo])
    Settodo('');
   
  }
  
  console.log(data)
  return (
    <div>
      <div className="container">
        <div className="subcontainer">
        <h1>Todo List</h1><br></br>
        <input value={todo} required="required"  onChange={(e)=>Settodo(e.target.value)} ></input>
        <button onClick={handlesubmit}>Add</button>
        <div>
        {data.map((data,i)=>{
          return (
            <p className="displaydata" key={i}>{data}</p>
          )
        })}
        </div>
        </div>
        
      </div>
    </div>
  );
};



export default Todo;
