import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import "./Home.css";

const Home = () => {
 
  
  
  const region=useParams();
  const myregion=region.all
  
  
  const [data, setdata] = useState(null);
  
  




  


  

  const [Url,seturl]=useState(`https://restcountries.com/v2/all?fields=name,europe,flag`)

  

  const fetchdata = () => {
    try {
      
      axios.get(Url).then((res) => {
        setdata(res.data);
      });
    } catch (error) {
      console.log("ERROR");
    }
  };
  
  const filterByRegion = () => {
    
    if(myregion === 'all') {
      console.log('correct')
    }else if(myregion==='asia'){
      seturl(`https://restcountries.com/v2/region/asia`)
      console.log('asia')
    }else{
      seturl(`https://restcountries.com/v2/region/europe`)
      console.log('europe')
    }
    
}

console.log(myregion)

  useEffect(() => {
    filterByRegion()
    fetchdata()
  },[myregion]);



  return (
    <div>

      

      <div className="cards">
        {data &&
          data.map((item, i) => (
            <div  key={i}>
              <div className="card">
                
                <div><img src={item.flag} alt=""></img></div>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.region}</p>
                  </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
