import React, { useEffect, useState } from 'react'
import { NavLink, useNavigate } from 'react-router-dom'
import './Register.css'
const Register = (props) => {

  

  const [initialValues, setInitialValues] = useState({
    name: "",
    email: "",
    password: "",
  });

  
 
  let navigate = useNavigate();
  function HandleLogin (e){
    e.preventDefault();
    setFormValues((prevFormValues) => [...prevFormValues, initialValues]);
    navigate("/signin");
  }

  const [formValues, setFormValues] = useState([]);

  useEffect(() => {
    localStorage.setItem("formValues", JSON.stringify(formValues));
  }, [formValues]);

  
  return (
    <div className='logincontainer'>
    <div>
      <form className='formcontainer' onSubmit={HandleLogin}>
        <h2>Register</h2>
        <p>New user? <NavLink to='/signin' className='headerlinktext'>Create account</NavLink></p>
        <input className='inputbox' type='name' value='name' placeholder='   Username' onChange={setInitialValues({ ...initialValues, companyname: e.target.value })} required></input>
        <input className='inputbox' type='email'value='email' placeholder='   email' onChange={(e)=>setInitialValues(e.target.value)} required></input>
        <input className='inputbox' type='password'value='password' placeholder='   password' onChange={(e)=>setInitialValues(e.target.value)} required></input>
        <label><input type="checkbox" className='checkboxs' /><span className='checkboxtext'> Keep me signed in</span></label>
        <button type='submit'>Sign in</button>
        <p className='textbottom'><span>or sign in with</span></p>
        <div className='footerimgcontainer'>
        <div><img src={require('../Images/Icons/google.png')} className='footerimg' alt=''></img></div>
        <div><img src={require('../Images/Icons/facebook.png')} className='footerimg' alt=''></img></div>
        <div><img src={require('../Images/Icons/linkedin.png')} className='footerimg' alt=''></img></div>
        <div><img src={require('../Images/Icons/twitter.png')} className='footerimg' alt=''></img></div>
        </div>
      </form>
    </div>

    <div className='imgcontainer'>
      <img className='picture' src={require('../Images/mainimg/SignIn.png')} alt='loading'></img>
    </div>
  </div>
  )
}

export default Register
