import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import "./Home.css";

const Home = () => {
 
  const paramdata=useParams();
  setregions(paramdata.asia)

  console.log(regions)
  
  useEffect(()=>{
    
    
  filterByRegion()

  },[regions]);
  
  const [data, setdata] = useState(null);

  const [regions, setregions] = useState(null);
  

  const filterByRegion = () => {
    
  

    if(regions === 'all') {
      console.log('all')
    }else if(regions==='asia'){
      console.log('asia')
    }else{
      console.log('europe')
    }
    
}


  


  

  

  const Url = `https://restcountries.com/v2/all?fields=name,europe,flag`;











  
  const fetchdata = () => {
    try {
      
      axios.get(Url).then((res) => {
        setdata(res.data);
      });
    } catch (error) {
      console.log("ERROR");
    }
  };


  useEffect(() => {

   
    fetchdata();
  });



  return (
    <div>

      

      <div className="cards">
        {data &&
          data.map((item, i) => (
            <div  key={i}>
              <div className="card">
                
                <div><img src={item.flag} alt=""></img></div>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.region}</p>
                  </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
