import axios from 'axios'
import React, { useEffect, useState } from 'react'
import './App.css'

const topstories = `https://hacker-news.firebaseio.com/v0/newstories.json?print=pretty`;

const newStoriesUrl = 'https://hacker-news.firebaseio.com/v0';

const Api = () => {



//   const fetchdata=(url)=>{
//     return new Promise ((resolve, reject)=>{
//         fetch(url)
//         .then((res)=>res.json())
//         .then((data)=>resolve(data))
//         .then((err)=>reject(err))
//     });
//   };


  const showData =async ()=>{
    var data =await fetchdata(topstories);
    data.map(async (d)=>{
        let newsdata=await fetchdata(`${newStoriesUrl}/item/${d}.json`);
        console.log(newsdata);
    })
  }

  showData()






    const [results, setresults]=useState([])
    const [query, setquery]=useState('')
    const [loading, setloading]=useState(false)
    const [error,seterror]=useState(null);


    const fetchdata=async(url)=>{        
        try{
            const response = await axios.get(url);
        console.log(response.data)
        const newdata=response.data;
        console.log(newdata)
        } catch(err){
            seterror(err);
        }
    };

    

useEffect(()=>{
    fetchdata()
    getResults();
},[]);

const getResults=async()=>{
    setloading(true);
    try{
        const response = await axios.get(`https://hacker-news.firebaseio.com/v0/item/${newdata}.json`);
    setresults(response.data);
   
    } catch(err){
        seterror(err);
    }
    setloading(false);
};

const Handlesearch=(e)=>{
    e.preventDefault()
    getResults()
}

  return (
    <div className='container'>
        <form onSubmit={Handlesearch}>
        <input value={query} onChange={(e)=>setquery(e.target.value)}></input>
        <button type='submit'>GOO</button>
        </form>
        
        {loading ? (
            <div>loading.....</div>) :
            (
                <ul>
                {results.map((result)=>
                    <li key={result.id}>{result.title}</li>
                    )}
                    </ul>
            )
        }

        {error && <div>{error.message}</div>}
        
        </div>
  )
}

export default Api
