import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import "./Home.css";

const Home = () => {
 
  

  const filterByRegion = () => {
    if(region === '') {
      console.log('first')
    }
    
}
  


  const [data, setdata] = useState(null);
  const [region, setregion] = useState('all');
  

  const Url = `https://restcountries.com/v2/region/europe`;











  
  const fetchdata = () => {
    try {
      
      axios.get(Url).then((res) => {
        setdata(res.data);
      });
    } catch (error) {
      console.log("ERROR");
    }
  };


  useEffect(() => {

   
    fetchdata();
  });



  return (
    <div>

      

      <div className="cards">
        {data &&
          data.map((item, i) => (
            <div  key={i}>
              <div className="card">
                
                <div><img src={item.flag} alt=""></img></div>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.region}</p>
                  </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
