import axios from "axios";
import React, { useEffect, useState } from "react";
import "./Home.css";

const Home = () => {
  const Url = "https://restcountries.com/v2/all?fields=name,region,flag";

  const [data, setdata] = useState(null);
  const fetchdata = () => {
    try {
      axios.get(Url).then((res) => {
        setdata(res.data);
      });
    } catch (error) {
      console.log("ERROR");
    }
  };

  useEffect(() => {
    fetchdata();
  });

  console.log(data);

  return (
    <div>

<div>
      <nav className="navbar">
        <label className="navbar-toggle" id="js-navbar-toggle" for="chkToggle">
          <i className="fa fa-bars"></i>
        </label>
        <NavLink to="/region/all" className="logo">
          Countries
        </NavLink>
        <input type="checkbox" id="chkToggle"></input>
        <ul className="main-nav" id="js-menu">
          <li>
            <NavLink to="/" className="nav-links">
              All
            </NavLink>
          </li>
          <li>
            <NavLink to="#" className="nav-links">
              Asia
            </NavLink>
          </li>
          <li>
            <NavLink to="#" className="nav-links">
              Europe
            </NavLink>
          </li>
        </ul>
      </nav>
    </div>

    

      <div className="cards">
        {data &&
          data.map((item, i) => (
            <div  key={i}>
              <div class="card">
                
                <div><img src={item.flag} alt=""></img></div>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.region}</p>
                  </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
