import React, { useEffect, useState } from 'react'
import "./App.css";
import axios from 'axios';





const newStoriesUrl = 'https://hacker-news.firebaseio.com/v0';

 


function Home (){


  const [results, setresults]=useState([])
  const [loading, setloading]=useState(false)


//   const getResults=async()=>{
    
//     try{
//         const response = await axios.get(`https://hacker-news.firebaseio.com/v0/newstories.json?print=pretty`);
//         setresults(response.data);
   
//     } catch(err){
//         console.log('erorr')
//     }
    
// };

// console.log(results);


const getStory = async (id) => {
  setloading(true);
  try {
    const story = await axios.get(`${newStoriesUrl}/item/${id}.json`);
    return story;
  } catch (error) {
    console.log('Error while getting a story.');
  }
  setloading(false);
};

const getStories = async () => {
  try {
    const { data: storyIds } = await axios.get(
      `${newStoriesUrl}/newstories.json`
    );
    const stories = await Promise.all(storyIds.slice(0, 30).map(getStory));
    setresults(stories);
  } catch (error) {
    console.log('Error while getting list of stories.');
  }
};




useEffect(()=>{
 
 
  getStories()

  
},[]);


results.map((result)=> console.log(result.data.id))
console.log(results)


const Handlesearch=(e)=>{
  e.preventDefault()
 
}


  return (
    <div>
        <div className='container'>
        <form onSubmit={Handlesearch}>
        <input value={query} onChange={(e)=>setquery(e.target.value)}></input>
        <button type='submit'>GOO</button>
        </form>
        
        {loading ? (
            <div>loading.....</div>) :
            (
                <ul>
                {results.map((result)=>
                    <li key={result.id}>{result.data.title}</li>
                    )}
                    </ul>
            )
        }

        {error && <div>{error.message}</div>}
        
        </div>
    </div>
  )
}


export default Home
