import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import "./Home.css";

const Home = () => {
  const regions=useParams([]);

  console.log(regions.all);
  set(regions.all);
  



   


 



  const [data, setdata] = useState(null);
  const [region, setregion] = useState('all');
  

  useEffect(() => {

    const Url = `https://restcountries.com/v2/all?fields=name,${region.all},flag`;

    const fetchdata = () => {
      try {
        axios.get(Url).then((res) => {
          setdata(res.data);
        });
      } catch (error) {
        console.log("ERROR");
      }
    };

    fetchdata();
  },[setdata]);



  return (
    <div>

      

      <div className="cards">
        {data &&
          data.map((item, i) => (
            <div  key={i}>
              <div class="card">
                
                <div><img src={item.flag} alt=""></img></div>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.region}</p>
                  </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
