import React, { useEffect, useState } from 'react'
import "./App.css";
import axios from 'axios';

const Search = () => {



  const [results, setresults]=useState([])
  const [loading, setloading]=useState(false)
  const [error,seterror]=useState(null);
  const [query, setquery]=useState('react')

  const [stories, setstories]=useState('')

  console.log(stories)








const getResults=async()=>{
  setloading(true);
  try{
      const response = await axios.get(`https://hn.algolia.com/api/v1/search_by_date?query=${query}${stories}`);
      https://hn.algolia.com/?dateRange=all&page=0&prefix=true&query=&sort=byPopularity&type=all'
  setresults(response.data.hits);
  console.log(response.data.hits)
 
  } catch(err){
      seterror(err);
  }
  setloading(false);
};


useEffect(()=>{
 
 
  getResults()
  
 
},[]);





const Handlesearch=(e)=>{
  e.preventDefault()
  getResults()
  
 
}

function storychange(e){
  setstories(e.target.value)
  getResults()
  
}


  return (
    <div>
        <div className='container'>
        <form onSubmit={Handlesearch}>
        <input value={query} onChange={(e)=>setquery(e.target.value)}></input>
        
        <select name="stories" onChange={storychange}>
  <option value="">All</option>
  <option value="=story">story</option>
  <option value="=comment">comment</option>
</select>

<select name="popularity" id="popularity">
  <option value="popularity">popularity</option>
  <option value="Date">Date</option>
</select>

<select name="All time" id="All time">
  <option value="Last24h">Last 24h</option>
  <option value="Date">past week</option>
  <option value="mercedes">Past Month</option>
  <option value="mercedes">Past year</option>
  <option value="mercedes">Custom range</option>
</select>



        <button type='submit'>GOO</button>
        </form>
        
        {loading ? (
            <div>loading.....</div>) :
            (
                <ul>
                {results.map((result)=>
                    <li key={result.objectID}>{result.story_title}</li>
                    )}
                    </ul>
            )
        }

        {error && <div>{error.message}</div>}
        
        </div>
    </div>
  )
}

export default Search
