import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import "./Home.css";

const Home = () => {
 
  
  
  const region=useParams();
  const myregion=region.all

  const [data, setdata] = useState(null);
  
  const [urldata, seturldata] = useState('all?fields=name,europe,flag');

  const Url=`https://restcountries.com/v2/${urldata}`;

  


  


  const fetchdata = () => {
    try {
      filterByRegion()
      
      axios.get(Url).then((res) => {
        setdata(res.data);
      });
      
    } catch (error) {
      console.log("ERROR");
    }
  };
  
  
  
  const filterByRegion = () => {
    
    if(myregion === 'all') {
      
      seturldata('all?fields=name,europe,flag')
      console.log('correct')
    }else if(myregion==='asia'){
      seturldata('region/asia')
      console.log('region/asia')
    }else{
      seturldata('region/europe')
      console.log('europe')
    }
    
}

  




  useEffect(() => {
    
    

  fetchdata()
   
    console.log(urldata)
    
  },[myregion]);



  return (
    <div>

      

      <div className="cards">
        {data &&
          data.map((item, i) => (
            <div  key={i}>
              <div className="card">
                
                <div><img src={item.flag} alt=""></img></div>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.region}</p>
                  </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
