import React, { useState } from "react";


import "./Main.css";

const Todo = () => {


  
  const [todo, Settodo] = useState('');
  const [data, setdata]=useState([])


  function handlesubmit (e){

    setdata([...data,todo])
    Settodo('');
   
  }
  
  console.log(data)
  return (
    <div>
      <div className="container">
        <div className="subcontainer">
        <h1>Todo List</h1><br></br>
        <input value={todo} onChange={(e)=>Settodo(e.target.value)} required></input>
        <button onClick={handlesubmit}>Add</button>
        <div>
        {data.map((data,i)=>{
          return (
            <p className="displaydata" key={i}>{data}</p>
          )
        })}
        </div>
        </div>
        
      </div>
    </div>
  );
};



export default Todo;
