import React from 'react'

const Search = () => {


  
  const [results, setresults]=useState([])
  const [loading, setloading]=useState(false)
  const [error,seterror]=useState(null);
  const [query, setquery]=useState('')





const getStory = async (id) => {
  
  try {
    const story = await axios.get(`${newStoriesUrl}/item/${id}.json`);
    return story;
  } catch (error) {
    console.log('Error while getting a story.');
    seterror(error);
  }
  
};

const getStories = async () => {
  setloading(true)
  try {
    const { data: storyIds } = await axios.get(
      `${newStoriesUrl}/newstories.json`
    );
    const stories = await Promise.all(storyIds.slice(0, 30).map(getStory));
    setresults(stories);
    
  } catch (error) {
    console.log('Error while getting list of stories.');
    
  }
  
  setloading(false)
  
};


const getResults=async()=>{
  setloading(true);
  try{
      const response = await axios.get(`https://hn.algolia.com/api/v1/search_by_date?query=${query}`);
  setresults(response.data);
 
  } catch(err){
      seterror(err);
  }
  setloading(false);
};


useEffect(()=>{
 
 
  getStories()

  
},[]);





const Handlesearch=(e)=>{
  e.preventDefault()
  getResults()
 
}


  return (
    <div>
        <div className='container'>
        <form onSubmit={Handlesearch}>
        <input value={query} onChange={(e)=>setquery(e.target.value)}></input>
        <button type='submit'>GOO</button>
        </form>
        
        {loading ? (
            <div>loading.....</div>) :
            (
                <ul>
                {results.map((result)=>
                    <li key={result.data.id}>{result.data.title}</li>
                    )}
                    </ul>
            )
        }

        {error && <div>{error.message}</div>}
        
        </div>
    </div>
  )
}

export default Search
