import axios from "axios";
import React, { useEffect, useState } from "react";
import { NavLink, useParams } from "react-router-dom";
import Navbar from "../Navbar/Navbar";

import "./Home.css";

const Home = () => {
 
  
  
  const region=useParams();
  const myregion=region.all

  const [data, setdata] = useState(null);
  
  const [urldata, seturldata] = useState('all?fields=name,europe,flag');

  const Url=`https://restcountries.com/v2/${urldata}`;

  


  


 
  
  
  const filterByRegion = () => {
    
    if(myregion === 'all') {
      
      
      console.log('correct')
    }else if(myregion==='asia'){
      seturldata('region/asia')
      console.log('region/asia')
    }else{
      seturldata('region/europe')
      console.log('europe')
    }
    
}

  useEffect(() => {
    
    
    const fetchdata = () => {
      try {
        filterByRegion()
        
        axios.get(Url).then((res) => {
          setdata(res.data);
        });
        
      } catch (error) {
        console.log("ERROR");
      }
    };
    
  fetchdata()
   
    console.log(urldata)


  },[myregion,urldata]);

  const Handleall=()=>{
    seturldata('all?fields=name,europe,flag')
    console.log('hii')
  }
  const Handleasia=()=>{
    console.log('hii')
  }
  const Handleeurope=()=>{
    console.log('hii')
  }



  return (
    
    <>
    <div>
      <nav className="navbar">
        <label className="navbar-toggle" id="js-navbar-toggle" for="chkToggle">
          <i className="fa fa-bars"></i>
        </label>
        <NavLink to='/' className="logo">
          Countries
        </NavLink>
        <input type="checkbox" id="chkToggle"></input>
        <ul className="main-nav" id="js-menu">
          <li>
            <NavLink onClick={Handleall} className="nav-links">
              All
            </NavLink>
          </li>
          <li>
            <NavLink onClick={Handleasia} className="nav-links">
              Asia
            </NavLink>
          </li>
          <li>
            <NavLink onClick={Handleeurope} className="nav-links">
              Europe
            </NavLink>
          </li>
        </ul>
      </nav>
    </div>

      

      <div className="cards">
        {data &&
          data.map((item, i) => (
            <div  key={i}>
              <div className="card">
                
                <div><img className="flagimg" src={item.flag} alt=""></img></div>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.region}</p>
                  </div>
              </div>
            </div>
          ))}
      </div>
    </>
  );
};

export default Home;
