import React, { useEffect, useState } from 'react'
import "./Main.css";
import axios from 'axios';


const Search = () => {



  const [results, setresults]=useState([])
  const [loading, setloading]=useState(false)
  const [error,seterror]=useState(null);
  const [query, setquery]=useState('')
  const [stories, setstories]=useState('')
  const [sortby, setsortby]=useState('popularity')
  const [pagecount, setpagecount]=useState(0)

  console.log(results)

  const Increment=()=>{
    setpagecount(pagecount+1);
    getResults()
  }

const getResults=async()=>{
  setloading(true);
  try{           
   
      const response = await axios.get(`https://hn.algolia.com/api/v1/search?query=${query}&page=1`);
  setresults(response.data.hits);
  console.log(response.data.hits)
 
  } catch(err){
      seterror(err);
  }
  setloading(false);
};


useEffect(()=>{
 
 
  getResults()
  
 
},[]);





const Handlesearch=(e)=>{
  e.preventDefault()
  getResults()
  
 
}

function storychange(e){
  setstories(e.target.value)
  getResults()
  
}

function statechange(e){
  setsortby(e.target.value)
  getResults()
  
}





  return (
    <div>


<div >
        <div >
        <form onSubmit={Handlesearch} className='searchcontainer'>
        <input value={query} onChange={(e)=>setquery((e.target.value).toLowerCase())} className='searchinput' placeholder='  search..'></input>
        <button type='submit' className='searchtbtn'>SEARCH</button>
        </form> 
        </div>      
<div>

<div className='selectcontainer'>
<select name="stories"  className='selectbox' onChange={storychange}>
  <option value="all">All</option>
  <option value="story">story</option>
  <option value="comment">comment</option>
</select>

<select name="popularity"  className='selectbox' onChange={statechange}>
  <option value="popularity">popularity</option>
  <option value="byDate">Date</option>
</select>

<select name="All time" className='selectbox' >
  <option value="Last24h">Last 24h</option>
  <option value="Date">past week</option>
  <option value="mercedes">Past Month</option>
  <option value="mercedes">Past year</option>
  <option value="mercedes">Custom range</option>
</select>
</div>
</div>


      
        
        {loading ? (
            <div className='loading'>loading.....</div>) :
            (
                // <ul>


                <div  className="stories-container">

                {results.map((result)=>
                    <>
                <a href={result.url} className="card-anchor" target="_blank" rel="noreferrer">
                  <article className="story">
                  <p className="title">
                    {result.title}
                  </p>
                  <p className="text">{result.ERWERWERWEarticle}</p>
                  <p className="story-footer">
                   {result.descendants} comments
                  </p>
                </article>
                </a>

                </>
                    )}




                    


<div>
        <button onClick={Increment}>Load more</button>
        </div>


</div>


                    
            )
        }
        

        {error && <div>{error.message}</div>}
        
        </div>


        
            </div>
  )
}

export default Search
